mapper.select:
SELECT d.`name` AS masterName, d.model_code as masterCode, c.qual_name as qualName, c.qual_code as qualCode FROM prs_version_master a LEFT JOIN prs_ver_mas_qual b ON a.id = b.ver_mas_id LEFT JOIN prs_model_qual c ON b.qual_code = c.qual_code LEFT JOIN prs_model_master d ON d.model_code = a.model_code
1=1 and a.prj_id=#{prjId}
and a.model_code in
#{item}
and a.`status`=1 and b.`status`=1 and c.`status`=1 and d.`status`=1 order by d.model_code

SELECT a.* FROM prs_model_master a LEFT JOIN prs_version_master b ON a.model_code = b.model_code LEFT JOIN prs_project_versions c ON c.id = b.prj_id
1=1 and b.prj_id = #{versionId}
and ( a.model_code like concat("%",#{searchData},"%") or a.`name`like concat("%",#{searchData},"%") )
and c.time_value = #{dataYear}
