mapper.select:
SELECT qualinfo.plus_modelid as masterId, qualinfo.plus_version as versionId, qualinfo.plus_ruleid as plusRuleid, qualinfo.plus_qualid as plusQualid, qualinfo.prefix as prefix, qualinfo.data_year as dataYear, qualinfo.id, version.`name` as versionName, model_master.`name` AS masterName, qualinfo.tar_type as tarType, qual.qual_name as qualName, qual.qual_code as qualCode, qualinfo.plus_unit as unit, evd.`name` as evdName, evd.`code` as evdCode, cast(qualinfo.pt_times as char) as ptTimes, qualinfo.tar_qualid as tarQualid, qualinfo.tar_unit AS tarUnit, qualinfo.tar_qualname AS tarQualname, qualinfo.plus_qualcode AS plusQualcode FROM transpre_qualinfo_3rd qualinfo LEFT JOIN prs_project_versions version ON version.id = qualinfo.plus_version LEFT JOIN prs_model_master model_master ON model_master.id = qualinfo.plus_modelid LEFT JOIN prs_model_qual qual ON qual.id = qualinfo.plus_qualid LEFT JOIN bas_evd_info evd ON evd.id = qualinfo.plus_ruleid and qualinfo.plus_ruleid != 0 where 1= 1
and qualinfo.data_year = #{useYear}
and qualinfo.tar_type = #{tarType}
and qualinfo.plus_modelid = #{masterId}
and qualinfo.plus_version = #{versionId}
AND qualinfo.`plus_qualcode` LIKE concat(#{searchData},'%') OR qualinfo.`tar_qualname` LIKE concat(#{searchData},'%') OR qualinfo.tar_qualid LIKE concat(#{searchData},'%') OR EXISTS ( SELECT 1 FROM prs_model_qual tmp WHERE tmp.id = qualinfo.plus_qualid AND tmp.qual_name LIKE concat(#{searchData},'%') )
ORDER BY qualinfo.id desc LIMIT #{page},#{pageSize}

SELECT count(1) FROM transpre_qualinfo_3rd qualinfo
WHERE 1=1
and qualinfo.data_year = #{useYear}
and qualinfo.tar_type = #{tarType}
and qualinfo.plus_modelid = #{masterId}
and qualinfo.plus_version = #{versionId}
AND qualinfo.`plus_qualcode` LIKE concat(#{searchData},'%') OR qualinfo.`tar_qualname` LIKE concat(#{searchData},'%') OR qualinfo.tar_qualid LIKE concat(#{searchData},'%') OR EXISTS ( SELECT 1 FROM prs_model_qual tmp WHERE tmp.id = qualinfo.plus_qualid AND tmp.qual_name LIKE concat(#{searchData},'%') )

SELECT qualinfo.plus_modelid as masterId, qualinfo.plus_version as versionId, qualinfo.plus_ruleid as plusRuleid, qualinfo.plus_qualid as plusQualid, qualinfo.prefix as prefix, qualinfo.data_year as dataYear, qualinfo.id, version.`name` as versionName, model_master.`name` AS masterName, qualinfo.tar_type as tarType, qual.qual_name as qualName, qual.qual_code as qualCode, qualinfo.plus_unit as unit, evd.`name` as evdName, evd.`code` as evdCode, cast(qualinfo.pt_times as char) as ptTimes, qualinfo.tar_qualid as tarQualid, qualinfo.tar_unit AS tarUnit, qualinfo.tar_qualname AS tarQualname, qualinfo.plus_qualcode AS plusQualcode FROM transpre_qualinfo_3rd qualinfo LEFT JOIN prs_project_versions version ON version.id = qualinfo.plus_version LEFT JOIN prs_model_master model_master ON model_master.id = qualinfo.plus_modelid LEFT JOIN prs_model_qual qual ON qual.id = qualinfo.plus_qualid LEFT JOIN bas_evd_info evd ON evd.id = qualinfo.plus_ruleid and qualinfo.plus_ruleid != 0 where 1= 1
and qualinfo.data_year = #{useYear}
and qualinfo.tar_type = #{tarType}
and qualinfo.plus_modelid = #{masterId}
and qualinfo.plus_version = #{versionId}
AND qualinfo.`plus_qualcode` LIKE concat(#{searchData},'%') OR qualinfo.`tar_qualname` LIKE concat(#{searchData},'%') OR qualinfo.tar_qualid LIKE concat(#{searchData},'%') OR EXISTS ( SELECT 1 FROM prs_model_qual tmp WHERE tmp.id = qualinfo.plus_qualid AND tmp.qual_name LIKE concat(#{searchData},'%') )
ORDER BY qualinfo.id desc
